INSERT INTO `products` (`id`, `name`, `seo_title`, `seo_keywords`, `seo_description`, `code`, `price`, `description`, `available`, `photo`, `index`, `label`, `form`) VALUES
(1, 'Комплект коричневих свічок', 'Комплект коричневих свічок', 'різьблена свічка ручної роботи К2, свічка Львів, свічка Самбір, свічка Дрогобич, свічка Добромиль, свічка Тручкавець', 'Різьблена свічка К2, виготовлена вручну з високоякісного харчового парафіну П-2 у студії Марії Герасимлюк. Червоний, зелений, жовтий, білий, фіолетовий, рожевий, синій, коричневий та інші кольори', 'К2', '550.00', 'Свічки з комплекту окремо:\r\n1) Свічка висотою 25см(ліва) - 430грн.\r\n2) Свічка висотою 16см(права) - 120грн.\r\n- Обидві свічки виконані в одинакових тонах - коричневий і білий колір\r\n- Матеріали: харчовий парафін П-2, стеарин, свічковий лак, намистини; \r\nМожливі інші кольори під замовлення', 1, '1.jpg', 1, '', ''),
(2, 'Свічка червона', 'Свічка червона', 'різьблена свічка ручної роботи У131, свічка Львів, свічка Самбір, свічка Дрогобич, свічка Добромиль, свічка Тручкавець', 'Різьблена свічка У131, виготовлена вручну з високоякісного харчового парафіну П-2 у студії Марії Герасимлюк. Червоний, зелений, жовтий, білий, фіолетовий, рожевий, синій, коричневий та інші кольори', 'У131', '90.00', 'Опис:\r\n- кольори: червоний, білий;\r\n- висота: 10 см;\r\n- Матеріали: харчовий парафін П-2, стеарин, свічковий лак; \r\nМожливі інші кольори під замовлення', 1, '2.jpg', 2, '', ''),
